import random
from flask import Flask, render_template, request

app = Flask(__name__)

print(__name__)

@app.route('/')
def index():    # 뷰 함수
    return 'bye world!'

@app.route('/hello/')
def hello():    # 뷰 함수
    return '반갑습니다!'

@app.route('/greeting/<string:name>')
def greeting(name):
    return f'반갑습니다! {name}님!'

@app.route('/cube/<int:num>')
def cube(num):
    result = num ** 3
    return str(result)

# 메뉴 리스트에서 랜덤으로 인원 수 만큼 메뉴 골라서 출력해주기
@app.route('/lunch/<int:people>')
def lunch(people):
    menu = ['사내식당', '라면', '햄버거', '초밥', '치킨', '빵', '덮밥', '회', '전', '김밥']
    order = random.sample(menu, people)
    return str(order)

@app.route('/html_tag')
def html_tag():
    return '<h1>안녕하세요!!</h1>'

@app.route('/html_line')
def html_line():
    return """
    <h1>여러 줄 보내기</h1>
    <ul>
        <li>1번</li>
        <li>2번</li>
    </ul>
    """

@app.route('/html_render')
def html_render():
    return render_template('index.html')

@app.route('/html_name/<name>')
def html_name(name):
    return render_template('hello.html', name=name)

@app.route('/html_cube/<int:num>')
def html_cube(num):
    return render_template('cube.html', num1=num, num2=num**3)

# 저녁 메뉴 랜덤 뽑기
# /dinner로 요청이 들어왔을 때
# 저녁 메뉴에서 하나를 뽑아서 이미지와 메뉴 이름을 응답해 주자.
# 출력 예시
# 오늘의 저녁은 ??? 입니다.
# ??? 이미지
@app.route('/html_dinner')
def html_dinner():
    dinnerDic = {
        '짜장면': 'data:image/jpeg;base64,',
        '김밥': 'http://recipe1.ezmember.co.kr/cache/recipe/2016/03/08/0b0c25963c8be47cbdbdc02718849ae11.jpg',
        '초밥': 'data:image/jpeg;base64,',
        '오리고기' : 'data:image/jpeg;base64,'
    }
    idx = random.randrange(1, len(dinnerDic))
    dinnerList = list(dinnerDic.keys())
    return render_template('dinner.html', menu=dinnerList[idx], img=dinnerDic.get(dinnerList[idx]))

@app.route('/html_lotto')
def lotto():
    number_list = list(range(1, 10))
    num = random.choice(number_list)
    return render_template('lotto.html', num=num, lucky=number_list)

@app.route('/naver')
def naver():
    return render_template('naver.html')

@app.route('/google')
def google():
    return render_template('google.html')

@app.route('/ping')
def ping():
    return render_template('ping.html')

@app.route('/pong')
def pong():
    user_name = request.args.get('name')
    return render_template('pong.html', user_name=user_name)

@app.route('/ping_new')
def ping_new():
    return render_template('ping_new.html')

@app.route('/pong_new', methods=['POST'])
def pong_new():
    user_name = request.form.get('name')
    return render_template('pong_new.html', user_name=user_name)

@app.route('/ping_make_me')
def ping_make_me():
    return render_template('ping_make_me.html')

@app.route('/pong_make_me')
def pong_make_me():
    skill = ['행복', '사랑', '손재주', '커뮤니케이션', '사업성공', '요리실력', '코딩능력', '히어로의 능력', '외모', '근육', '무한히 먹을 수 있는 위', '명예', '빨리 일어나는 능력']
    selected_skill = random.sample(skill, 3)
    name = request.args.get('name')
    return render_template('pong_make_me.html', name=name, skill1=selected_skill[0], skill2=selected_skill[1], skill3=selected_skill[2])
# 이 파이썬 파일이 직접 실행된 코드람녀 if 문 코드를 동작한다.
if __name__ == '__main__':
    app.run(debug=True)
